/* eslint-env node, mocha */

import chai from "chai"; // eslint-disable-line
import "mocha";
import * as sinon from 'sinon';
import { promisify } from "util";
import chaiAsPromised from 'chai-as-promised';
import sinonStubPromise from 'sinon-stub-promise';
import { createConnection } from "serverless-typeorm";
import {
  getUserByIdHandler,
} from "../../src/handlers/handler";
import Challenge from "../../src/services/challenge";

const { expect } = chai;
const dummyActiveUser =  {   
  "_id": "5f7c26c4a7d5771adb959406", 
  "name": "Joe Smith",
  "email": "jsmith@gmail.com",
  "campaignId": "5f7c26d2f340427c28e91c70"
};

sinonStubPromise(sinon);
chai.use(chaiAsPromised);
let createConnectionStub;
let modelStub;

describe("Handler", async () => {
  afterEach(() => {
    if (modelStub && modelStub.restore) {
      modelStub.restore();
    }
    if (createConnectionStub && createConnectionStub.restore) {
      createConnectionStub.restore();
    }
  })
  describe("getUserByIdHandler()", () => {
    it("Successfully returns users", async () => {
        createConnectionStub = createConnection = sinon.stub().resolves({isConnected: true, close: sinon.stub()});
        const dummyEvent = {
          queryStringParameters: {
            id: "5f7c26c4a7d5771adb959406"
          },
        };
        modelStub = sinon.stub(Challenge.prototype, "findUserByID").resolves(dummyActiveUser);
        const func = promisify(getUserByIdHandler);
        const results = await func(dummyEvent, {});
        const body = JSON.parse(results.body);
        expect(body.data[0]._id).to.equal("5f7c26c4a7d5771adb959406");
    });
  })
});

import { Connection, createConnection } from "serverless-typeorm";
import Challenge from "../services/challenge";

const getUserByIdHandler = async (event: any, context: any) => {
  let connection: Connection;
  try {
    connection = await createConnection();
    const model = new Challenge(connection);
    const item = await model.findUserByID(event.queryStringParameters.id);
    await connection.close();
    return {
      body: JSON.stringify(
        {
          input: item,
        },
        null,
        2,
      ),
      statusCode: 200,
    };
  } catch (error) {
    if (connection) {
      await connection.close();
    }
    return {
      body: JSON.stringify(
        {
          error,
          message: "Error!",
        },
        null,
        2,
      ),
      statusCode: 200,
    };
  }
};

export { getUserByIdHandler }; // eslint-disable-line

import ErrorCustom from "@admixltd/admix-error";
import { Connection, getMongoManager } from "serverless-typeorm";
import {
  Campaign,
  Users,
} from "../entities/entities";
/**
 * class Challenge.
 */
class Challenge {
  /**
   * Class property set to an instance
   *
   */
  private db: Connection;
  private manager: any;
  constructor(db: Connection) {
    if (!db) {
      throw new ErrorCustom("DB not connected", 500, 100);
    }
    this.db = db;
    this.manager = getMongoManager();
  }

  /**
   * Find User by ID
   *
   * @param {string} userId
   * User Id
   *
   * @return {Promise}
   * Resolves with User or false if no user;
   *
   */
  // tslint:disable-next-line
  public async findUserByID(_id: string) {
    try {
      const repo = await this.db.getRepository(Users);
      return await repo.findOne({ _id });
    } catch (error) {
      return Promise.reject(error);
    }
  }

  /**
   * returns 200 object
   */
  public example() {
    return [
      {
        success: 200,
      },
    ];
  }
}

export default Challenge;

export * from "./abstract.entity";
export * from "./campaignEntity";
export * from "./userEntity";

import { Expose, Transform } from "class-transformer";
import {
  Column,
  CreateDateColumn,
  ObjectIdColumn,
  UpdateDateColumn,
} from "serverless-typeorm";

export interface IAbstractModel extends Document {
  createdAt?: Date;
  updatedAt?: Date;
  id: string;
}

export abstract class AbstractEntity {
  @Expose()
  @ObjectIdColumn()
  public _id: string;

  @Expose({ groups: ["user", "admin", "ssp"] })
  @CreateDateColumn({ nullable: true })
  public createdAt?: Date;

  @Expose({ groups: ["user", "admin", "ssp"] })
  @UpdateDateColumn({ nullable: true })
  public updatedAt?: Date;

  @Expose({ groups: ["admin", "ssp"] })
  @Column()
  public isDeleted: boolean;

  @Expose({ groups: ["admin"] })
  @Column()
  // tslint:disable-next-line:variable-name
  public _v: string;
  constructor() {
    this.isDeleted = false;
    this._v = process.env.DOCUMENT_VERSION;
  }
}

import { Expose } from "class-transformer";
import "reflect-metadata";
import { Column, Entity } from "serverless-typeorm";
import { AbstractEntity } from "./abstract.entity";

interface ICreative {
  name: string;
  type: "banner" | "video" | "interactive";
}

@Entity("campaign")
export class Campaign extends AbstractEntity {
  @Expose()
  @Column()
  public name: string;

  @Column()
  public type: "awareness" | "traffic" | "sales";

  @Column()
  public creatives: ICreative[];

  constructor(props: any) {
    super();
    this.creatives = props && props.creatives ? props.creatives : [];
  }
}

import { Expose } from "class-transformer";
import "reflect-metadata";
import { Column, Entity } from "serverless-typeorm";
import { AbstractEntity } from "./abstract.entity";

@Entity("users")
export class Users extends AbstractEntity {
  @Expose()
  @Column()
  public campaignId: string;

  @Column()
  public name: string;

  @Column()
  public email: string;

}

/* eslint-env node, mocha */

import chai from "chai"; // eslint-disable-line
import "mocha";
import * as sinon from 'sinon';
import chaiAsPromised from 'chai-as-promised';
import sinonStubPromise from 'sinon-stub-promise';
import { getMongoManager } from "serverless-typeorm";
import Challenge from "../../src/services/challenge";
let sandbox;
let queryStub;
const { expect } = chai;
sinonStubPromise(sinon);
chai.use(chaiAsPromised);
getMongoManager = sinon.stub();

describe("Challenge", () => {
  describe("Challenge Constructor", () => {
    it("constructs", (done) => {
        const result = new Challenge({});
        expect(result).to.have.own.property("db");
        expect(result).to.have.own.property("manager");
        done();
    });

    it("DB throws error if not provided", (done) => {
        try {
          new Challenge();
        } catch (error) {
          expect(error.message).to.equal("DB not connected");
        }

        done();
    });
  });

  describe("Challenge fn", () => {
    it("returns successObj", (done) => {
        const result = new Challenge({});

        const example = result.example();
        expect(example[0].success).to.equal(200);

        done();
    });
  });
});
